import {useState} from "react";

//Style
import './styles/style.css';
function Samurai(){
    const animationArr = [
            "Images/Samurai.png",
            "Images/Samurai2.png"
    ]
    const [currentFrameSource, setCurrentFrameSource] = useState(0);

    const [url] = useState(animationArr[currentFrameSource])

    setTimeout(args => {
        let anim = currentFrameSource + 1;
        if(anim == animationArr.length){
            anim = 0;
        }
        setCurrentFrameSource(anim)
        setUrl
    }, 1000)
    return (
        <td>
            <img src={url}></img>
        </td>
    )
}

export default Samurai
